@bot.message_handler(commands=['start'])
def start(message):
    bot.send_message(message.chat.id, "–ü—Ä–∏–≤–µ—Ç —è –ø–æ–º–æ–≥—É —Ç–µ–±–µ –≤—ã–±—Ä–∞—Ç—å –∫–∞—Ä—å–µ—Ä—É üëª. –ù–∞–ø–∏—à–∏ '—Ç–≤–æ—Ä—á–µ—Å–∫–∏–π', '—Ç–µ—Ö–Ω–∏—á–µ—Å–∫–∏–π' –∏–ª–∏ '—Å–æ—Ü–∏–∞–ª—å–Ω—ã–π'.üò∞")

@bot.message_handler(func=lambda message: True)
def handle_message(message):
    user_input = message.text.lower()

    if user_input in careers:
        recommendations = ", ".join(careers[user_input])
        bot.send_message(message.chat.id, f"–í–æ—Ç –ø—Ä–æ—Ñ–µ—Å—Å–∏–∏ –≤ –Ω–∞–ø—Ä–∞–≤–ª–µ–Ω–∏–∏ '{user_input}': {recommendations}.")
    else:
        bot.send_message(message.chat.id, "–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –Ω–∞–ø–∏—à–∏ '—Ç–≤–æ—Ä—á–µ—Å–∫–∏–π', '—Ç–µ—Ö–Ω–∏—á–µ—Å–∫–∏–π' –∏–ª–∏ '—Å–æ—Ü–∏–∞–ª—å–Ω—ã–π'ü§¨.")

bot.infinity_polling()